:root {
  --color-primary: #2584ff;
  --color-secondary: #00d9ff;
  --color-accent: #ff3400;
  --color-headings: #1b0760;
  --color-body: #918ca4;
  --color--border: #ccc;
  --border--radius: 30px;
}
*, *::after, *::before{
    box-sizing: border-box;
}

/* typography */

::selection{
  background-color: var(--color-primary);
  color: white;
}
html{
    font-size: 62.5%;
}

body{
    font-family: inter, Arial, Helvetica, sans-serif;
    color: var(--color-body);
    font-size: 2.4rem;
    line-height: 1.5;

}

h1,h2,h3{
    color: var(--color-headings);
    margin-bottom: 1rem;
    line-height: 1.2;
}

h1{
    font-size: 7rem;
}

h2{
    font-size: 4rem;
}

h3{
font-size: 3rem;
}

p{
    margin-top: 0;
}

@media screen and (min-width: 1024px){
    body{
        font-size: 1.8rem;
    }
    h1{
        font-size: 80px;
    }
    h2{
        font-size: 40px;
    }
    h3{
        font-size: 24px;
        font-weight: 500;
    }
}
/* links */
a{
    text-decoration: none;
}

.link-arrow{
    color: var(--color-accent);
    font-weight: bold;
    font-size: 2rem;
    text-transform:uppercase
}
.link-arrow::after{
    content: "-->";
    margin-left: 5px;
    transition: margin 0.15s;
}
.link-arrow:hover::after{
    margin-left: 10px;
}

@media screen and (min-width: 1024px){
    .link-arrow{
     font-size: 1.5rem;
    }
}

/*badges*/

.badge{
    border-radius: 20px;
    color: white;
    font-size: 2rem;
    font-weight: 600;
    padding: 0.5rem 2rem;
    white-space: nowrap;
}

.badge--primary{
background-color: var(--color-primary);
}

.badge--secondary{
background-color: var(--color-secondary);
}

.badge--small{
font-size: 1.6rem;
}

@media screen and (min-width: 1024px){
    .badge{
     font-size: 1.5rem;
    }
    .badge--small{
     font-size: 1.2rem;
    }
  }

/* lists*/

.list{
    list-style: none;
    padding-left: 0; /* cancelling out the default left padding that is given to lists*/
  }

  .list--tick{
    list-style-image: url(/images/tick.svg);
    padding-left: 30px;
    color: var(--color-headings);
}

.list--tick .list__item{
    padding-left: 0.5rem;
    margin-bottom: 1rem;
}

.list--inline .list__item{
    display: inline-block;
    margin-right: 2rem;
}

@media screen and (min-width: 1024px) {
    .list--tick .list__item {
      padding-left: 0;
    }
  }

  /*icons*/
  .icon{
   width:40PX;
   height: 40px;
  } 
  icon--small{
    width: 30px;
    height: 30px;
  }

  .icon--primary{
   fill: var(--color-primary);
  }
  .icon--white{
    fill: white;
  }

  .icon-container{
  background: #f3f9fa;
  width: 64px;
  height: 64px;
  border-radius: 100%;
  display: inline-flex;
  justify-content: center;
  align-items: center;
  }

  .icon-container--accent{
    background-color: var(--color-accent);
  }

  /* buttons */
  .btn{
      border-radius: 30px;
      padding: 1.5rem 4vw;
      font-weight: 600;
      font-size: 1.7rem;
      white-space: nowrap;
      cursor: pointer;
      text-transform: uppercase;
      border: 0;
      margin: 1rem 0;
      outline: 0;
  }

  .btn--primary{
  color: white;
  background-color: var(--color-primary);
  }
  .btn--primary:hover{
   background-color: #4694f8;
  }

  .btn--secondary{
    color: white;
    background-color: var(--color-secondary);
  }
  .btn--secondary:hover{
      background-color: #7ae7fa;
  }

  .btn--accent{
    color: white;
    background-color: var(--color-accent);
  }
  .btn--accent:hover{
      background-color: #ff663f;
  }

  .btn--outline{
      border: 2px solid var(--color-headings);
      color:var(--color-headings);
      background-color: white;
  }
  .btn--outline:hover{
      background-color: var(--color-headings);
      color:white;
  }
  .btn--stretched{
    padding-left: 6rem;
    padding-right: 6rem;
  }

  .btn--block{
      width: 100%;
      display: inline-block;
      text-align: center;
      vertical-align: middle;
  }
  .btn .icon{
    width: 2rem;
    height: 2rem;
    margin-right: 1rem;
  }

  @media screen and (min-width: 1024px) {
    .btn {
      font-size: 1.5rem;
    }
  }

  /*cards*/

  .card{
  border-radius: 7px;
  box-shadow: 0 0 20px 10px #f3f3f3;
  overflow: hidden;
  }

  .card__header, .card__body{
    padding: 2rem 3rem;
  }

  .card--primary .card__header{
      color: white;
      background-color: var(--color-primary);
  }

  .card--secondary .card__header{
      color:white;
      background-color: var(--color-secondary);
  }
  .card--secondary .badge--secondary{
    background-color: #02cdf1;
  }
  .card--primary .badge--primary{
   background-color: #126de4;
  }

  /*plans*/

  .plan__name{
   color:white;
   font-size: 2.4rem;
   font-weight: 500;
   margin: 0;
  }

  .plan__price{
   font-size: 6rem;
  }

  .plan__billing__cycle{
   font-size: 2.4rem;
   font-weight: 300;
   margin-right: 1rem;
   opacity: 0.8;
}
  .plan__description{
   display: block; 
   letter-spacing: 1px;
   font-size: 2rem;
   font-weight: 300;
  }

  .card__header .badge--secondary{
      background-color: #02cdf1;
  }

  .plan .list__item {
    margin-bottom: 2rem;
  }

  .plan--popular .card__header::before{
      content: url(/images/popular.svg);
      width: 40px;
      display: inline-block;
      position: absolute;
      top: -6px;
      right: 5%;
  }
  .plan--popular .card__header{
      position: relative;
  }


   @media screen and (min-width: 1024px) {
    .plan__name {
      font-size: 1.4rem;
    }

    .plan__price {
      font-size: 5rem;
    }

    .plan__billing-cycle {
      font-size: 1.6rem;
    }

    .plan__description {
      font-size: 1.7rem;
    }
  }

  /*media objects*/
  .media{
      display: flex;
      margin-bottom: 4rem;
  }
  .media__title{
      margin-top: 0;
  }

  .media__body {
    margin: 0 2rem;
  }

  .media__image {
    margin-top: 1rem;
  }

  /* Quotes */
.quote {
    font-size: 3rem;
    font-style: italic;
    color: var(--color-body-darker);
    line-height: 1.3;
  }

  .quote__text::before {
    content: open-quote;
  }

  .quote__text::after {
    content: close-quote;
  }

  .quote__author {
    font-size: 3rem;
    font-weight: 500;
    font-style: normal;
    margin-bottom: 0;
  }

  .quote__organization {
    color: var(--color-headings);
    opacity: 0.4;
    font-size: 2rem;
    font-style: normal;
  }

  .quote__line {
    position: relative;
    bottom: 10px;
  }

  @media screen and (min-width: 1024px) {
    .quote {
      font-size: 2rem;
    }

    .quote__author {
      font-size: 2.4rem;
    }

    .quote__organization {
      font-size: 1.6rem;
    }
  }

  /* Grids */
.grid {
  display: grid;
}

@media screen and (min-width: 768px) {
  .grid--1x2 {
    grid-template-columns: repeat(2, 1fr);
  }
}

@media screen and (min-width: 1024px) {
  .grid--1x3 {
    grid-template-columns: repeat(3, 1fr);
  }
}

/*testimonials*/
.testimonial{
  padding: 3rem;
}

.testimonial__img > img{
  width: 100%;
}

.testimonial__img{
  position: relative
}

.testimonial__img > .icon-container{
  position: absolute;
  top: 3rem;
  right: -32px;
}

@media screen and ( min-width: 768px){
  .testimonial .quote,
  testimonial .quote__author{
    font-size: 2.4rem;
  }
  .testimonial .quote{
    margin-left: 6rem;
  }
}

/*callouts*/
.callout{
  border-radius: 5px;
  padding: 4rem;
}

.callout--primary{
  background-color: var(--color-primary);
  color:white;
}

.callout__heading{
  margin-top: 0;
  color: #fff;
  font-size: 3rem;
}

.callout .btn{
  justify-self: center;
  align-self: center;
}
.callout__content{
  text-align: center;
}

@media screen and ( min-width: 768px){
  .callout__content{
    text-align: left;
  }
  .callout .btn{
    justify-self: left;
    margin: 0 2rem;
  }
  .callout .grid--1x2{
    grid-template-columns: 1fr auto;
  }
}

/*collapsibles*/
.collapsible__header{
  display: flex;
  justify-content: space-between;
}

.collapsible__heading{
  margin-top: 0;
  font-size: 3rem;
}

.collapsible__chevron{
  transform: rotate(-90deg);
  transition: transform 0.3s;
}

.collapsible__content{
  max-height:0;
  overflow: hidden;
  opacity: 0;
  transition: all 0.3s;
}
.collapsible--expanded .collapsible__content{
  max-height: 100vh;
  opacity: 1;
}
.collapsible--expanded .collapsible__chevron{
  transform: rotate(0);
}
/*blocks*/
.block__heading{
  margin-top: 0;
}
.block{
  padding: var(--padding-vertical) 2rem;
  --padding-vertical: 6rem;
}
.block--dark{
  background-color: black;
  color:var(--color--border)
}
.block--dark h1,
.block--dark h2,
.block--dark h3{
  color: white;
}

.block--skewed--right{
  clip-path: polygon(100% 0, 100% 100%, 0 90%, 0 0);
  padding: calc(var(--padding-vertical) + 4rem);
}
.block--skewed--left{
  clip-path: polygon(100% 0, 100% 90%, 0 100%, 0 0);
  padding: calc(var(--padding-vertical) + 4rem);
}
.block__header{
  text-align: center;
  margin-bottom: 4rem;
}
.container{
  max-width: 1140px;
  margin: 0 auto;
}
/*nav bar*/
.nav{
  background-color: black;
  display: flex;
  justify-content: space-between;
  flex-wrap: wrap;
  padding: 0 1rem;
  align-items: center;
}
.nav__list{
  width: 100%;
  margin: 0;
}
.nav__item{
  color: #d2d0db;
  padding: 0.5rem 2rem;
  border-bottom: 1px solid #222;
}
.nav__item > a{
  color:#d2d0db;
  transition: color 0.3s;
}
.nav__item > a:hover{
  color: white;
}
.nav__toggler{
  opacity: 0.5;
}
.nav.collapsible--expanded .nav__toggler{
  opacity: 1;
  box-shadow: 0 0 0 3px #666;
  border-radius: 100%;
  transition: box-shadow 0.15s;
  cursor: pointer;
}
.nav__brand{
  transform: translateY(3px);
}

@media screen and (min-width: 768px){
  .nav__toggler{
    display: none;
  }
  .nav__list{
    width: auto;
    display: flex;
    font-size: 1.6rem;
    max-height: 100%;
    opacity: 1;
  }
  .nav__item{
    border: 0;
  }
}
/*hero*/

.hero__tagline{
  font-size: 2rem;
  color: #b9c3cf;
  letter-spacing: 1px;
  margin: 2rem 0 5rem;
}

.hero__image{
  width: 100%;
}
@media screen and (min-width: 768px){

  .hero{
    padding-top: 0;
  }

  .hero__content{
    text-align: left;
    align-self: center;
  }
}
/*domain block*/

.block-domain .input--group {
  box-shadow: 0 0 30px 20px #e6ebee;
  border: 0;
  margin: 4rem auto;
  max-width: 670px;
}

.block-domain__prices {
  display: grid;
  grid-template-columns: repeat(2, 1fr);
  grid-template-rows: repeat(2, 6rem);
  font-size: 2rem;
  font-weight: 600;
  justify-items: center;
  max-width: 700px;
  margin: 0 auto;
}

@media screen and (min-width: 768px) {
  .block-domain__prices {
    grid-template-columns: repeat(auto-fit, minmax(10rem, 1fr));
  }
}

/*plans block*/
.block-plans .grid{
  gap: 8rem 4rem;
}

.block-plans .card{
  max-width: 500px;
  margin: 0 auto;
}
.plan--popular{
  transform: scale(1.1);
}
.plan:hover{
  transform:scale(1.05);
}
.plan--popular:hover{
  transform: scale(1.15);
}
.plan{
  transition: transform 0.2s ease-out;
}
/*feature block*/
.feature_heading{
  margin: 1rem 0;
}

feature{
  gap: 4rem;
  margin: 12rem 0;
}

.feature__image{
  width: 100%
}
.feature:first-of-type{
  margin-top: 6rem;
}

/*showcase block*/
.block-showcase__img{
  width: 100%;
}

@media screen and (min-width: 768px){
  .block-showcase__img{
    width: auto;
    justify-self: end;
    max-width: 700px;
  }
  .block-showcase .grid{
    grid-template-columns: 50% 50%;
  }
}
/*footer*/
.footer{
  background-color: #232323;
}

.footer__section{
  padding: 2rem;
  border-bottom: 1px solid #393939;
}
.footer__heading{
  text-transform: uppercase;
  font-weight: 600;
}
.footer__brand{
  margin-top: 4rem;
  text-align: center;
}
.footer__copyright{
  font-size: 2.1rem;
  color: #fff;
  opacity: 0.3;
}
.footer a{
  color: #777;
  transition: color 0.3s;
}
.footer a:hover{
  color: white;
}

.footer__section .list{
  margin: 0;
}
.footer__brand img{
  width: 100%;
  max-width: 230px;
}

@media screen and (min-width: 768px){
.footer__sections{
  grid-template-columns: repeat(auto-fit, minmax(10rem, 1fr));
}
.footer .collapsible__chevron{
  display: none;
}
.footer .collapsible__content{
  opacity: 1;
  max-height: 100%;
}
.footer__brand{
  order: -1;
  margin-top: 1rem;
}
.footer__copyright{
  font-size: 1.5rem;
}
.footer__section{
  border: 0;
}
.footer__heading{
  font-size: 1.6rem;
}
}
